import{_ as d,o as r,c as e,ak as i}from"./chunks/framework.CGzjHEBf.js";const f=JSON.parse('{"title":"附录D：术语表","description":"介绍常见术语的英文译名与中文解释。","frontmatter":{"title":"附录D：术语表","description":"介绍常见术语的英文译名与中文解释。"},"headers":[],"relativePath":"guide/appendix/D-glossary.md","filePath":"zh/guide/appendix/D-glossary.md"}'),a={name:"guide/appendix/D-glossary.md"};function n(o,t,s,l,c,g){return r(),e("div",null,[...t[0]||(t[0]=[i('<h1 id="附录d-术语表" tabindex="-1">附录D：术语表 <a class="header-anchor" href="#附录d-术语表" aria-label="Permalink to “附录D：术语表”">​</a></h1><table tabindex="0"><thead><tr><th>英文术语</th><th>中文译名</th><th>解释</th></tr></thead><tbody><tr><td>k-space</td><td>k 空间</td><td>在 MRI 中指采集的原始频域数据矩阵，通过傅里叶变换生成图像。</td></tr><tr><td>T1-weighted</td><td>T1 加权</td><td>MRI 序列一种对比方式，强调纵向弛豫特性，结构清晰。</td></tr><tr><td>T2-weighted</td><td>T2 加权</td><td>MRI 序列另一种对比方式，强调横向弛豫特性，液体／水肿显示明显。</td></tr><tr><td>Windowing (Window Width/Level)</td><td>灰度窗／窗宽窗位</td><td>在 CT 或 X 光影像显示时，用于调整灰度范围与中点优化对比。</td></tr><tr><td>Flat-field correction</td><td>平场校正</td><td>校正影像中探测器／照明不均或响应不一致导致的暗斑或条纹伪影。</td></tr><tr><td>SENSE (Sensitivity Encoding)</td><td>SENSE（灵敏度编码）</td><td>MRI 并行成像技术，利用多个线圈敏感度信息实现扫描加速。</td></tr><tr><td>Parallel Imaging</td><td>并行成像</td><td>使用多个接收线圈同步采样以减少扫描时间或提高分辨率。</td></tr><tr><td>Echo Time (TE)</td><td>回波时间</td><td>MRI 中 RF 脉冲到信号采集起始之间的时间间隔，影响图像对比。</td></tr><tr><td>Repetition Time (TR)</td><td>重复时间</td><td>MRI 中脉冲循环间隔时间，影响信号恢复和对比。</td></tr><tr><td>Flip Angle</td><td>翻转角</td><td>MRI 射频脉冲使核磁共振倾斜角度，影响信号强度／对比。</td></tr><tr><td>Field of View (FOV)</td><td>视野</td><td>成像覆盖的解剖区域或体积大小，通常以 mm 表示。</td></tr><tr><td>Voxel</td><td>体素</td><td>三维像素，医学影像数据中的最小单位。</td></tr><tr><td>Slice Thickness</td><td>切片厚度</td><td>在断层扫描（CT／MRI）中每张图像的厚度，影响体积重建与部分体积效应。</td></tr><tr><td>Partial Volume Effect</td><td>部分体积效应</td><td>当一个体素包含多种组织类型时，信号混合导致精度降低。</td></tr><tr><td>Artifact</td><td>伪影／伪像</td><td>图像中由设备、患者运动、信号处理等引入的非真实组织结构。</td></tr><tr><td>Signal-to-Noise Ratio (SNR)</td><td>信噪比</td><td>图像中信号强度与噪声强度之比，衡量图像质量。</td></tr><tr><td>Contrast-to-Noise Ratio (CNR)</td><td>对比噪声比</td><td>病灶与背景组织之间信号差距与噪声之比，反映可识别性。</td></tr><tr><td>Reconstruction Kernel</td><td>重建滤波核</td><td>CT 重建时使用的滤波器类型，会影响锐利度和噪声水平。</td></tr><tr><td>Radiomics</td><td>放射组学</td><td>从影像中提取大量定量特征（纹理、形状、强度等）用于建模分析。</td></tr><tr><td>Segmentation</td><td>分割</td><td>将影像像素／体素分类为不同结构（如器官、水肿、肿瘤）以进行定量分析。</td></tr><tr><td>Registration</td><td>配准／注册</td><td>将来自不同时间点或不同模态的影像对齐至同一空间以方便比较。</td></tr><tr><td>Deep Learning</td><td>深度学习</td><td>使用深度神经网络（如 CNN、Transformer）对影像数据进行分析／预测。</td></tr><tr><td>Transfer Learning</td><td>迁移学习</td><td>将一个任务训练好的模型应用于另一个相关任务以减少训练成本。</td></tr><tr><td>Multimodal Learning</td><td>多模态学习</td><td>融合多种影像模态（如 MRI + PET + 基因数据）进行联合分析。</td></tr><tr><td>Overfitting</td><td>过拟合</td><td>模型在训练集上表现很好但在新数据上泛化能力差的现象。</td></tr><tr><td>Dice Coefficient</td><td>Dice 系数</td><td>衡量分割任务中预测与真实区域重叠程度的指标。</td></tr><tr><td>Intersection over Union (IoU)</td><td>交并比</td><td>用于分割评价：预测与真实区域交集／并集比值。</td></tr><tr><td>Sensitivity (Recall)</td><td>敏感性／召回率</td><td>分类任务中真正例被正确识别的比例。</td></tr><tr><td>Specificity</td><td>特异性</td><td>真负例被正确识别的比例。</td></tr><tr><td>ROC Curve</td><td>ROC 曲线</td><td>显示假阳性率与真阳性率关系的曲线，用于分类评估。</td></tr><tr><td>Hounsfield Unit (HU)</td><td>Hounsfield 单位</td><td>CT 中表示组织密度的单位：水为 0，空气约 –1000。</td></tr><tr><td>Contrast Agent</td><td>造影剂</td><td>在成像前注入或服用用于增强某些组织或血管对比。</td></tr><tr><td>Dual-energy CT</td><td>双能量 CT</td><td>利用两种不同能量的 X-射线对比增强成像材质分辨能力。</td></tr><tr><td>Ground-glass Opacity (GGO)</td><td>磨玻璃影</td><td>肺部影像中轻度增密、但血管纹理仍可见的不透明征象。</td></tr><tr><td>Mass</td><td>肿块</td><td>较大体积（通常 &gt;3 cm）可能异常结构或肿瘤，需要观察与鉴别。</td></tr><tr><td>Nodule</td><td>结节</td><td>较小体积（通常 &lt;3 cm）结构，需随访是否增长或恶变。</td></tr><tr><td>Pleural Effusion</td><td>胸腔积液</td><td>胸膜腔内液体积聚，在影像中表现为液平面或模糊边界。</td></tr><tr><td>Cardiomegaly</td><td>心脏扩大</td><td>心影/胸廓比增大，提示心脏结构异常。</td></tr><tr><td>Calcification</td><td>钙化</td><td>组织或病灶内钙沉积，在 CT 或 X 光中高密度可见。</td></tr><tr><td>Infarct</td><td>梗死</td><td>血供中断导致组织坏死，在 MRI／CT 中有典型影像表现。</td></tr><tr><td>Ring Enhancement</td><td>环状强化</td><td>对比增强影像中病灶边缘强化而中心低信号／低密度，常见转移瘤或脑脓肿。</td></tr><tr><td>Spiculated Margin</td><td>刺状边缘</td><td>肿物边缘呈辐射状尖刺突起，提示侵袭性或恶性可能。</td></tr><tr><td>Ghosting</td><td>幽影／重影</td><td>MRI 图像中特别在相位编码方向，由患者运动等引起的条纹或重复影。</td></tr><tr><td>Flip Angle</td><td>翻转角</td><td>MRI 射频脉冲使核磁共振倾斜角度，影响信号强度／对比。</td></tr><tr><td>Fourier Transform</td><td>傅里叶变换</td><td>将空间域信号转换为频率域（如 k-空间到图像域）的数学变换。</td></tr><tr><td>Coil Sensitivity</td><td>线圈敏感度</td><td>多通道 MRI 接收线圈不同空间位置的响应差异，用于并行成像。</td></tr><tr><td>Nyquist Frequency</td><td>奈奎斯特频率</td><td>采样理论中最低必须采样频率的一半，低于该频率会发生混叠。</td></tr><tr><td>Aliasing</td><td>混叠（重叠伪影）</td><td>当采样不足或 FOV 太小时，影像外部信号在图像中被重复或重叠。</td></tr></tbody></table>',2)])])}const T=d(a,[["render",n]]);export{f as __pageData,T as default};
